# makes plot, calculates average, finds minimum
def data_analysis(data_list):
    # give lowest a huge value
    lowest = 999999999
    sum = 0

    # if item lower than lowest, item becomes new lowest
    for item in data_list:
        if item < lowest:
            lowest = item

    # add item to sum
    for item in data_list:
        sum += item

    # divide sum by list length for average
    average = sum/len(data_list)

    # make hist of data
    plt.hist(data_list, bins=50)
    # print other findings
    print(f"This data wins the game after an average of {average} turns, with {lowest} turns as the fastest game.")

    plt.xlabel("turns until victory")
    plt.ylabel("frequency")
    plt.savefig("histogram.png")
